CREATE TABLE `orders` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT 'Идентификатор строки',
  `user_id` int unsigned NOT NULL COMMENT 'Ссылка на пользователя',
  `car_id` int unsigned NOT NULL COMMENT 'Ссылка на город проживания',
  `driver_id` int unsigned NOT NULL COMMENT 'Ссылка водителя',
  `amount` decimal(10,0) DEFAULT NULL COMMENT 'Стоимость заказа',
  `tariff_id` int unsigned DEFAULT NULL COMMENT 'Ссылка на тариф',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'Время создания строки',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Время обновления строки',
  PRIMARY KEY (`id`),
  KEY `orders_user_id_fk` (`user_id`),
  KEY `orders_car_id_fk` (`car_id`),
  KEY `orders_driver_id_fk` (`driver_id`),
  KEY `orders_tariffes_id_fk` (`tariff_id`),
  CONSTRAINT `orders_car_id_fk` FOREIGN KEY (`car_id`) REFERENCES `cars` (`id`),
  CONSTRAINT `orders_driver_id_fk` FOREIGN KEY (`driver_id`) REFERENCES `drivers` (`id`),
  CONSTRAINT `orders_tariffes_id_fk` FOREIGN KEY (`tariff_id`) REFERENCES `tariffes` (`id`),
  CONSTRAINT `orders_user_id_fk` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=101 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Заказы';
